import React, { Component } from "react";
import { Button } from "semantic-ui-react";
class Tareas extends Component {
  _remove() {
    if (this.props.onRemove) this.props.onRemove();
  }
  render() {
    return (
      <li>
        <div className="icon">
          <img src={`../images/${this.props.data.image}`} alt="hambuerger" />
        </div>
        <div className="name">
          <span className="item-name">{this.props.data.name}</span>
          <span className="item-price">{this.props.data.portion}</span>
        </div>
        <div className="price">
          <h3>${this.props.data.price}</h3>
        </div>
        <Button className="remove" onClick={this._remove.bind(this)}>
          <i className="semantic-ui-react">close</i>
        </Button>
      </li>
    );
  }
}

export default Tareas;
