import React, { Component } from "react";
import { Button, Input } from "semantic-ui-react";
class Buton extends Component {
  constructor() {
    super();
    this.createItem = this.createItem.bind(this);
    this.deleteItem = this.deleteItem.bind(this);

    this.itemInput = React.createRef();

    this.state = {
        list: []
    };
}

createItem() {
    const itemValue = this.itemInput.current.value;

    if (itemValue) {
        this.setState({
            list: [
                ...this.state.list,
                itemValue
            ]
        });

        this.itemInput.current.value = '';
        this.itemInput.current.focus();
    }
}

deleteItem(itemKey) {
    this.setState(({ list }) => ({
        list: list.filter((item, index) => index !== itemKey)
    }));
}
  render() {
    const val = {
      list: this.state.list
    };

    return (
      <div>
        <input
          type="text"
          id="myInput"
          placeholder="Añade tu tarea..."
          ref={this.Entrada}
        />
        <Button basic
          color="blue"
          className="ingresar" onClick={val.list.length}>
          Añadir Tareas
        </Button>

        <p>Lista de pendientes({val.list.length})</p>
      </div>


    );
  }
}

export default Buton;
