import React, { Component } from "react";
import { Button, Input } from "semantic-ui-react";
class Buton extends Component {
  constructor() {
    super();

    this.Entrada = React.createRef();

    this.state = {
      list: []
    };
  }

  render() {
    const val = {
      list: this.state.list
    };

    return (
      <div id="myDIV" className="header">
        <h2>PEndiente ({val.list.length})</h2>
        <Input
          type="text"
          id="myInput"
          placeholder="Añadade  tu tarea..."
          ref={this.Entrada}
        />
        <Button basic color="Blue" className="addBtn">
          AñadirTareas
        </Button>
      </div>
    );
  }
}

export default Buton;
